import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from 'react-redux';
import M from 'materialize-css/dist/js/materialize.min.js';
import { toMyShoppingCart } from '../actions/allActions';


function Navbar() {
  const [showSearchIcon, setShowSearchIcon] = useState(true)
  const [screenSize, setScreenSize] = useState('')
  const cartCounter = useSelector(state => state.cartCounter);
  const dispatch = useDispatch();


  useEffect(() => {
    if(window.innerWidth > 1200) {
      setScreenSize('medium');
    }
    else {
      setScreenSize('small')
    }

    window.addEventListener('resize', async e => {
      if(window.innerWidth > 1200) {
        setScreenSize('medium');
      }
      else {
        setScreenSize('small')
      }
    })
  }, []);

  useEffect(() => {
    window.addEventListener('click', async e => {
      if(!e.target.closest('#searchIcon') && !e.target.closest('#search')) {
        setShowSearchIcon(true)
      }
    })

  }, []);

  document.addEventListener('DOMContentLoaded', function() {
    let elems = document.querySelectorAll('.sidenav');
    M.Sidenav.init(elems);
  });

  const hideSearchIcon = () => {
    setShowSearchIcon(false)
  }

  document.addEventListener('DOMContentLoaded', function() {
    var elems = document.querySelectorAll('.autocomplete');
    let data = {
      "Apple": null,
      "Microsoft": null,
      "Google": 'https://placehold.it/250x250'
    }
    var instances = M.Autocomplete.init(elems, {data});
  });

  return (
    <div className="navbar">
      <nav className="nav-extended">
        <div className="nav-wrapper">
          <div className="container">



          <div class="row">
            <div class="col s12">
              <div class="row">
                <div class="input-field col s12">
                  <i class="material-icons prefix">search</i>
                  <input type="text" id="autocomplete-input" class="autocomplete" />
                  <label for="autocomplete-input">Autocomplete</label>
                </div>
              </div>
            </div>
          </div>



            {!showSearchIcon && screenSize === 'small'?
              <div className="row">
                <div className="col s12">
                  <div className="left input-field">
                    <input id='search' type="search" required />
                    <label className="label-icon" htmlFor="search"><i className="material-icons">search</i></label>
                    <i className="material-icons close-icon">close</i>
                  </div>
                </div>
              </div>
              : ''
            }

            {showSearchIcon &&
              <div className="row">

                <div className="col s3 xl5 menu-col">
                  <a href="/" data-target="mobile-demo" className="left sidenav-trigger sidenav-trigger-menu">
                    <i className="material-icons">menu</i>
                  </a>

                  {showSearchIcon && <label id="searchIcon" onClick={hideSearchIcon} className="left label-icon" htmlFor="search"><i className="material-icons">search</i></label> }

                  {!showSearchIcon || screenSize === 'medium'?
                    <div className="left input-field">
                      <input id='search' type="search" required />
                      <label className="label-icon" htmlFor="search"><i className="material-icons">search</i></label>
                      <i className="material-icons close-icon">close</i>
                    </div>
                    : ''
                  }
                </div>

                <div className="col s3 xl2 logo-col">
                  <a href="/" className="center brand-logo">Bluma<i className="right material-icons filter_vintage">filter_vintage</i></a>
                </div>

                <div className="col s3 xl4">
                  <ul id="nav-mobile">
                    <li><a href="om-oss">Om oss</a></li>
                    <li><a href="hitta-oss">Hitta oss</a></li>
                  </ul>

                </div>

                <div className="right col s3 xl1 cart-col">
                  <div className="right shopping_cart_counter">
                    <span onClick={() => dispatch(toMyShoppingCart())}>
                      <a href="/mina-inköp">
                        <i className="left material-icons shopping_cart">shopping_cart</i>
                      </a>
                    </span>
                    <p className="left counter">{cartCounter}</p>
                  </div>
                </div>

              </div>
            }

          </div>
        </div>
      </nav>

      <ul className="sidenav" id="mobile-demo">
        <li><a href="om-oss"><h5>Om oss</h5></a></li>
        <li><a href="hitta-oss"><h5>Hitta oss</h5></a></li>
      </ul>
    </div>
  );

}

export default Navbar;
