import React, { useEffect, useState } from "react";
import M from 'materialize-css/dist/js/materialize.min.js';


function Navbar() {
  const [largeSearchBar, setLargeSearchBar] = useState(false);


  useEffect(() => {
    window.addEventListener('click', e => {

      if(e.target.closest('#search')) {
        console.log('hello');
        e.preventDefault();
        setLargeSearchBar(false);
      }
    })
  }, []);

  document.addEventListener('DOMContentLoaded', function() {
    let elems = document.querySelectorAll('.sidenav');
    M.Sidenav.init(elems);
  });

  // const eventListener = () => {
  //   window.addEventListener('click', e => {

  //     if(!e.target.closest('#search')) {
  //       e.preventDefault();
  //       setLargeSearchBar(false);
  //     }
  //   })
  // }

  window.addEventListener('click', e => {

    if(e.target.closest('#search')) {
      console.log('hello');
      e.preventDefault();
      setLargeSearchBar(false);
    }
  })

  const enlargeSearchBar = () => {
    setLargeSearchBar(true)
  }

  return (
    <div className="navbar">
      <nav className="nav-extended">
        <div className="nav-wrapper">
          <div className="container">
            <div className="row">

              <div className="col s5">
                <a href="/" data-target="mobile-demo" className="left sidenav-trigger sidenav-trigger-menu">
                  <i className="material-icons">menu</i>
                </a>
                <div className="left input-field">
                  <input id={largeSearchBar ? 'large-search-bar': 'search'} onClick={enlargeSearchBar} type="search" required />
                  <label className="label-icon" htmlFor="search"><i className="material-icons">search</i></label>
                  <i className="material-icons">close</i>
                </div>
              </div>

              <div className="col s2">
                <a href="/" className="center brand-logo">Logo</a>
              </div>

              <div className="col s4">
                <ul id="nav-mobile" className="right">
                  <li><a href="sass.html">Sass</a></li>
                  <li><a href="badges.html">Components</a></li>
                  <li><a href="collapsible.html">JavaScript</a></li>
                </ul>

              </div>

              <div className="col s1">
                <a href="/"><i className="right material-icons">shopping_cart</i></a>
              </div>

            </div>
          </div>
        </div>
      </nav>

      <ul className="sidenav" id="mobile-demo">
        <li><a href="sass.html">Sass</a></li>
        <li><a href="badges.html">Components</a></li>
        <li><a href="collapsible.html">JavaScript</a></li>
      </ul>
    </div>
  );

}

export default Navbar;
