import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from 'react-redux';
import { increment, decrement, updateCart } from '../actions/allActions';


function Home() {
  const counter = useSelector(state => state.counter);
  const isLogged = useSelector(state => state.isLogged);
  const dispatch = useDispatch();
  const myShoppingCart = useSelector(state => state.myShoppingCart);
  const [searchResults, setSearchResults] = useState([]);
  const [myCart, setMyCart] = useState([]);

  useEffect(()=> {

    async function getData() {
      let data = await fetch('/api/flowers');
      data = await data.json();

      setSearchResults(data);
    }

    getData();
  }, [])

  useEffect(()=> {

    async function getData() {
      let data = await fetch('/api/shopping-cart');
      data = await data.json();
      console.log(data);

      let flowerIds = [];
      for(let flowerId of data) {
        flowerIds.push(flowerId.id)
      }
      console.log(flowerIds)
    }

    getData();
  }, [])

  useEffect(() => {
    if(myShoppingCart===0){return}
    console.log(myCart);

    async function postData() {
      let response = await fetch('/api/shopping-cart', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(myCart)
      });
      console.log(response.json());
      // return await response.json();
    }

    postData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  },[myShoppingCart]);

  const addToCart = e => {
    myCart.push(e.target.id);
    setMyCart(myCart);
    console.log(myCart)
    dispatch(updateCart(myCart.length));
  }

  const removeFromCart = e => {
    if(myCart.includes(e.target.id)) {
      let flowerIndex = myCart.indexOf(e.target.id);
      myCart.splice(flowerIndex, 1);
      setMyCart(myCart)
      console.log(myCart);
      dispatch(updateCart(myCart.length));
    }
  }

  return (
    <div className="home container">
      <h1>
        Startsidan!
      </h1>

      <div className="row">
        {searchResults.map((flower) => 
          <div className="col s12 m6 l4" key={flower.id}>
            <div className="card">
              <div className="card-image">
                <img src={flower.url} alt="blomma" />
                <button className="btn-floating halfway-fab waves-effect waves-dark white add">
                  <i onClick={addToCart} id={flower.id} className="black-text material-icons">add</i>
                </button>
                <button className="btn-floating halfway-fab waves-effect waves-dark white remove">
                  <i onClick={removeFromCart} id={flower.id} className="black-text material-icons">remove</i>
                </button>
              </div>
              <div className="card-content">
                <span className="card-title">{flower.title}</span>
                <span className="price">{flower.price} kr</span>
              </div>
            </div>
          </div>
        )}

      </div>

      <h1>{counter}</h1>
      <button onClick={() => dispatch(increment(5))}>+</button>
      <button onClick={() => dispatch(decrement())}>-</button>
      <h3>{isLogged? 'hello':'bye'}</h3>
    </div>
  )
}

export default Home;
