export const increment = (nr) => {
  return {
    type: 'INCREMENT',
    payload: nr
  }
}

export const decrement = () => {
  return {
    type: 'DECREMENT'
  }
}

export const toMyShoppingCart = () => {
  return {
    type: 'TO-MY-SHOPPING-CART'
  }
}

export const updateCart = (cartLength) => {
  return {
    type: 'UPDATE-CART',
    payload: cartLength
  }
}
