import React, { useEffect } from 'react';
import { Route, Switch } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { updateCart } from '../actions/allActions';
import './App.css';
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import Home from "./components/Home";
import About from "./components/About";
import Contact from "./components/ShopingCart";
import FindUs from "./components/FindUs";


function App() {
  const dispatch = useDispatch();

  useEffect(()=> {

    async function getShoppingCart() {
      let shoppingCart = await fetch('/api/shopping-cart');
      shoppingCart = await shoppingCart.json();

      let flowerIds = [];
      for(let flower of shoppingCart) {
        flowerIds.push(flower.flower_id.toString())
      }
      console.log(flowerIds)
      dispatch(updateCart(flowerIds.length));
    }

    getShoppingCart();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  return (
    <div className="App">

      <Navbar />
      <main>
        <Switch>
          <Route exact path="/" component={Home} />
          <Route path="/om-oss" component={About} />
          <Route path="/hitta-oss" component={FindUs} />
          <Route path="/mina-inkÃ¶p" component={Contact} />
        </Switch>
      </main>

      <Footer />
    </div>
  );
}

export default App;
